import maya.cmds as cmdsimport maya.standalone as standalonestandalone.initialize(name='python')import sysimport oshomeDrive=os.environ["HOMEDRIVE"]pathAntiwave=homeDrive+"\\ANTIWAVE1.0\\"pathResources=pathAntiwave+"Resources\\"InstallPath = pathResources+"antiwave_install\\"TempPath = pathResources+"Temp\\" cmds.file(sys.argv[1], open=True, f=True)renderlayers= cmds.ls(type="renderLayer")w=[]h=[]fs=[]fe=[]layersID=[]for layer in renderlayers:       cmds.editRenderLayerGlobals(crl=layer)    w.append(cmds.getAttr("defaultResolution.width"))    h.append(cmds.getAttr("defaultResolution.height"))    fs.append(cmds.getAttr("defaultRenderGlobals.startFrame"))    fe.append(cmds.getAttr("defaultRenderGlobals.endFrame"))        layersID.append(layer)  a=open(TempPath+'dataForCoordinates.txt','w')a.write(str(layersID)+'*')a.write(str(w)+'*'+str(h)+'*')a.write(str(fs)+'*'+str(fe)+'*')a.close()